<style scoped lang="scss">
@import "../variables.scss";
@import "../main.scss";

.footer {
  background-color: $background-primary;

  &__top-wrapper {
    position: relative;
    display: flex;
    justify-content: space-between;
    padding: 60px 80px 50px;
    color: $color-primary;
  }

  &__top-wrapper::after {
    position: absolute;
    right: 30px;
    bottom: 0;
    left: 30px;
    content: "";
    border: 1px solid #4e4d93;
  }

  &__top-inner-wrapper-left {
    display: flex;
    width: 50%;
  }

  &__top-inner-wrapper-right {
    width: 50%;
  }

  &__list-wrapper {
    width: 30%;
  }

  &__list {
    padding-top: 10px;
  }

  &__form {
    display: flex;
    flex-wrap: wrap;
    padding-top: 15px;
  }

  &__buttom-wrapper {
    display: flex;
    justify-content: space-between;
    padding: 20px 80px;
    color: $color-primary;
  }

  &__buttom-inner-wrapper {
    display: flex;
  }

